            <Button variant="contained" color="primary" onClick={audioFile.play()}>
                <PlayArrowIcon/>
            </Button>

            <Button variant="contained" color="primary">
                <SkipNextIcon/>
            </Button>

        </div>
    )
}

export default ControlPanel;
