    let audioSource = 'Dont_Worry_Be_Happy.mp3';
    return (
      <div className='App'>
        <Header/>
        <SongImage />
        <ControlPanel audioSource={audioSource} />

        {/* <Slider/> */}
      </div>
    );
  }
}

export default App;
