import React, {Component} from 'react'
import Button from '@material-ui/core/Button'
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import PauseIcon from '@material-ui/icons/Pause';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';

class ControlPanel extends Component {

    constructor(props) {
        super(props);
        this.state = { play: false }
    }

    play = () => {
        if (this.state.play) {
            this.setState({ play: false });
            this.audio.pause();
        } else {
            this.setState({ play: true });
            this.audio.play();
        }
    }

    // const [state, setState] = useState({isplaying: false})
    // const isplaying = state.isplaying;


    // function play(audioFile) {
    //     if(!isplaying) {
    //         audioFile.play()
    //         setState(!isplaying);
    //         audioFile.pause()
    //         setState(!isplaying);
    //     }
    // }

    render() {

        console.log(this.props.audioSource)
        let audioFile = new Audio(this.props.audioSource)

        return (

            <div>  
                <Button variant="contained" color="primary">
                    <SkipPreviousIcon/>
                </Button>

                <Button variant="contained" color="primary" onClick={() => this.play}>
                    <PlayArrowIcon/>
                </Button>

                <Button variant="contained" color="primary" onClick={() => audioFile.pause()}>
                    <PauseIcon/>
                </Button>

                <Button variant="contained" color="primary">
                    <SkipNextIcon/>
                </Button>

            </div>
        )
    }
}

export default ControlPanel;







